package cn.poile.ucs.auth.vo;import cn.poile.ucs.auth.entity.SysUser;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import java.util.ArrayList;import java.util.Collection;import java.util.List;/** * @author yaohw * @date 2019-09-25 16:12 */public class UserDetailImpl extends SysUser implements UserDetails {    private Collection<? extends GrantedAuthority> authorities;    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        return this.authorities;    }    @Override    public String getPassword() {        return this.getPassword();    }    @Override    public String getUsername() {        return this.getUsername();    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return true;    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return this.getEnable().equals(1);    }    public void setAuthorities(Collection<? extends GrantedAuthority> authorities) {        this.authorities = authorities;    }}